mapper.select:
/* AttendanceMApper.getAttendanceList */
SELECT A.EMAIL, B.NAME, A.DAY, A.IN_TIME, A.OUT_TIME, A.LATENESS_AT, A.LATENESS_REASON FROM TB_ATTENDANCE A LEFT OUTER JOIN TB_MEMBER B ON B.EMAIL = A.EMAIL WHERE A.DELETE_AT = 'N' AND B.QUIT_AT = 'N'
AND A.DAY = #{startDay}
AND A.DAY = #{endDay}
AND TO_DATE(A.DAY, 'YYYY-MM-DD') BETWEEN TO_DATE(#{startDay}, 'YYYY-MM-DD') AND TO_DATE(#{endDay}, 'YYYY-MM-DD')
AND A.LATENESS_AT = #{latenessAt}
AND ( A.EMAIL LIKE '%' || #{name} || '%' OR B.NAME LIKE '%' || #{name} || '%' )
ORDER BY TO_DATE(A.DAY, 'YYYY-MM-DD')DESC, TO_DATE(A.IN_TIME, 'HH24:MI')DESC, TO_DATE(A.OUT_TIME, 'HH24:MI') DESC

/* AttendanceMApper.getAttendanceListCnt */
SELECT COUNT(*) FROM TB_ATTENDANCE A LEFT OUTER JOIN TB_MEMBER B ON B.EMAIL = A.EMAIL WHERE A.DELETE_AT = 'N' AND B.QUIT_AT = 'N'
AND A.DAY = #{startDay}
AND A.DAY = #{endDay}
AND TO_DATE(A.DAY, 'YYYY-MM-DD') BETWEEN TO_DATE(#{startDay}, 'YYYY-MM-DD') AND TO_DATE(#{endDay}, 'YYYY-MM-DD')
AND A.LATENESS_AT = #{latenessAt}
AND ( A.EMAIL LIKE '%' || #{name} || '%' OR B.NAME LIKE '%' || #{name} || '%' )

mapper.update:
/* AttendanceMapper.updateOut */

UPDATE
	TB_ATTENDANCE
SET
	OUT_TIME = #{outTime},
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{email}
WHERE
	DAY = #{day}
	AND EMAIL = #{email}
