mapper.insert:
/* BoardMapper.insertFile */
INSERT INTO TB_FILE ( FILE_ID, FRST_REGIST_PNTTM, USE_AT ) VALUES ( SEQ_TB_FILE.NEXTVAL, SYSDATE, DEFAULT )
SELECT SEQ_TB_FILE.CURRVAL FROM DUAL

mapper.update:
/* BoardMapper.addViewNum */

UPDATE
	TB_BOARD
SET
	BOARD_VIEW = #{boardView}
WHERE
	BOARD_NO = #{boardNo}

/* BoardMapper.updateInsertFile */

UPDATE
	TB_BOARD
SET
	FILE_ID = #{fileId}
WHERE
	BOARD_NO = #{boardNo}

mapper.delete:
/* BoardMapper.deleteFile */

DELETE FROM
	TB_FILE_DETAIL
WHERE
	FILE_STORE = #{fileStore}
	AND RENAMED_FILE_NAME = #{renamedFile}
