mapper.insert:
/* CategoryBoardMapper.insertCategoryBoard */
INSERT INTO TB_CATEGORY ( CATEGORY_NO, CATEGORY_NAME, FRST_REGIST_PNTTM, FRST_REGISTER_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID, DELETE_AT, PROJECT_ID ) VALUES ( SEQ_TB_CATEGORY.NEXTVAL, #{title}, SYSDATE, #{loginEsntlId}, SYSDATE, #{loginEsntlId}, 'N', #{projectId} )
SELECT SEQ_TB_CATEGORY.CURRVAL FROM DUAL

mapper.update:
/* CategoryBoardMapper.deleteCategoryBoard */

UPDATE
	TB_CATEGORY
SET
	DELETE_AT = 'Y'
WHERE
	CATEGORY_NO = #{categoryNo}
	AND FRST_REGISTER_ID = #{email}
