mapper.insert:
/* ProjectMapper.insertProeject */
INSERT INTO TB_PROJECT ( PROJECT_ID, PROJECT_NM, PROJECT_MANAGER, FRST_REGIST_PNTTM, FRST_REGISTER_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID, USE_AT, START_DATE, END_DATE, NOTE ) VALUES ( SEQ_TB_PROJECT.NEXTVAL, #{projectNm}, #{projectManager}, SYSDATE, #{frstRegisterId}, SYSDATE, #{lastUpdusrId}, DEFAULT, #{startDate}, #{endDate}, #{note} )
SELECT SEQ_TB_PROJECT.CURRVAL FROM DUAL

mapper.select:
/* ProjectMapper.getMemberList */
SELECT A.EMAIL, A.NAME, A.PHONE, A.BIRTH, A.PASSWORD, A.DEPARTMENT, ( SELECT DETAIL_CODE_NAME FROM TB_COMMON_CODE_DETAIL WHERE DETAIL_CODE = A.DEPARTMENT
) AS DEPARTMENT_NAME, A.POSITION, ( SELECT DETAIL_CODE_NAME FROM TB_COMMON_CODE_DETAIL WHERE DETAIL_CODE = A.POSITION
) AS POSITION_NAME, A.EMAIL_KEY, A.EMAIL_VERIFY_PNTTM, A.QUIT_AT, A.ROLE_CODE, A.ZIP_CODE, A.ADDRESS, A.DETAIL_ADDRESS, A.ESNTL_ID FROM TB_MEMBER A WHERE EMAIL_VERIFY_PNTTM IS NOT NULL AND A.QUIT_AT = 'N' AND A.ESNTL_ID NOT IN (
SELECT ESNTL_ID FROM TB_PROJECT_USER WHERE PROJECT_ID = #{projectId} )
AND A.DEPARTMENT = #{department}
AND A.POSITION = #{position}
ORDER BY A.NAME

/* ProjectMapper.getProjectMemberList */
SELECT A.EMAIL, A.NAME, ( SELECT DETAIL_CODE_NAME FROM TB_COMMON_CODE_DETAIL WHERE DETAIL_CODE = A.POSITION
) AS POSITION_NAME, ( SELECT DETAIL_CODE_NAME FROM TB_COMMON_CODE_DETAIL WHERE DETAIL_CODE = A.DEPARTMENT
) AS DEPARTMENT_NAME, B.PROJECT_ROLE, A.ESNTL_ID FROM TB_MEMBER A LEFT OUTER JOIN TB_PROJECT_USER B ON B.ESNTL_ID = A.ESNTL_ID WHERE B.PROJECT_ID = #{projectId}
AND (A.NAME LIKE '%' || #{searchKeyword} || '%' OR A.EMAIL LIKE '%' || #{searchKeyword} || '%')
ORDER BY PROJECT_ROLE, NAME

mapper.update:
/* ProjectMapper.updateProjectRole */

UPDATE
	TB_PROJECT_USER
SET
	PROJECT_ROLE = #{role},
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{loginEsntlId}
WHERE
	PROJECT_ID = #{projectId}
	AND ESNTL_ID = #{esntlId}

/* ProjectMapper.updateProjectRoleManger */

UPDATE
	TB_PROJECT
SET
	PROJECT_MANAGER = #{esntlId},
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{loginEsntlId}
WHERE
	PROJECT_ID = #{projectId}

/* ProjectMapper.updateProjectManagerToUser */

UPDATE
	TB_PROJECT_USER
SET
	PROJECT_ROLE = 'U',
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{loginEsntlId}
WHERE
	PROJECT_ID = #{projectId}
	AND PROJECT_ROLE = 'M'

/* ProjectMapper.insertProjectMemo */

UPDATE
	TB_PROJECT
SET
	NOTE = #{note},
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{loginEsntlId}		
WHERE
	PROJECT_ID = #{projectId}

/* ProjectMapper.deleteProject */

UPDATE
	TB_PROJECT
SET
	USE_AT = 'N',
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{loginEsntlId}
WHERE
	PROJECT_ID = #{projectId}

mapper.delete:
/* ProjectMapper.deleteProjectMember */

DELETE FROM
	TB_PROJECT_USER
WHERE
	PROJECT_ID = #{projectId}
	AND ESNTL_ID = #{esntlId}
