mapper.update:
/* commonCode.updateCommonCode */

UPDATE 
	TB_COMMON_CODE
SET
	CODE_NAME = #{codeName},
	USE_AT = #{useAt},
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{email}
WHERE
	CODE = #{code}

/* commonCode.deleteCommonCode */

UPDATE
	TB_COMMON_CODE
SET
	USE_AT = 'N',
	LAST_UPDT_PNTTM = SYSDATE,
	LAST_UPDUSR_ID = #{email}
WHERE
	CODE = #{code}
